#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe" node "$basedir/../toast/toast-cli.mjs" $args
  } else {
    & "$basedir/NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe" node "$basedir/../toast/toast-cli.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe" node "$basedir/../toast/toast-cli.mjs" $args
  } else {
    & "NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe" node "$basedir/../toast/toast-cli.mjs" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
